mapper.select:
SELECT SNO, NAME, EMAIL
  FROM TB_MEMBER
 WHERE PASSWORD = #{password}

SELECT SNO, NAME, EMAIL
  FROM TB_MEMBER
 WHERE SNO = #{sno}

SELECT COUNT(*)
  FROM TB_MEMBER
 WHERE SNO = #{sno}
   AND PASSWORD = #{password}

mapper.update:
UPDATE TB_MEMBER SET
	NAME = #{name},
	EMAIL = #{email}
 WHERE SNO = #{sno}

UPDATE TB_MEMBER SET
	PASSWORD = #{password},
	SALT = #{salt}
 WHERE SNO = #{sno}
